CREATE TABLE IF NOT EXISTS `houses` (
  `id` varchar(55) NOT NULL,
  `owner` varchar(55) NOT NULL DEFAULT 'nil',
  `ownerName` varchar(255) NOT NULL DEFAULT 'nil',
  `prevowner` varchar(255) NOT NULL DEFAULT 'nil',
  `price` int(55) NOT NULL DEFAULT 150000,
  `isSpec` tinyint(2) NOT NULL DEFAULT 0,
  `locked` char(5) NOT NULL DEFAULT 'true',
  `door` varchar(55) NOT NULL,
  `shell` varchar(55) NOT NULL DEFAULT 'shell_lester',
  `draw` float NOT NULL DEFAULT 15,
  `failBuy` char(5) NOT NULL DEFAULT 'false',
  `purDate` longtext NOT NULL DEFAULT '[]',
  `keys` longtext NOT NULL DEFAULT '[]',
  `furniture` longtext NOT NULL DEFAULT '[]',
  `parkings` longtext NOT NULL DEFAULT '[]',
  `doors` longtext NOT NULL DEFAULT '[]',
  `garages` longtext NOT NULL DEFAULT '[]',
  `storage` longtext NOT NULL DEFAULT '[]',
  `wardrobe` longtext NOT NULL DEFAULT '[]',
  `garageType` varchar(55) NOT NULL DEFAULT 'persistent',
  `ownerDiscord` varchar(255) NOT NULL DEFAULT 'nil',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

CREATE TABLE `house_parking` (
  `plate` varchar(255) NOT NULL,
  `data` longtext NOT NULL DEFAULT '[]',
  PRIMARY KEY (`plate`)
);

ALTER TABLE `users`
	ADD COLUMN `furniture` longtext NOT NULL DEFAULT '[]',
	ADD COLUMN `wardrobe` longtext NOT NULL DEFAULT '[]',
	ADD COLUMN `homeIn` varchar(255) NOT NULL DEFAULT 'none'
;


